import React from "react";
import { useLocalStorage } from "./useLocalStorage";
const TodoContext = React.createContext()

function TodoProvider({ children }){
    const {item:todos,
        saveItem:saveTodos,
        loading,
        error} = useLocalStorage('TODOS_V2', []);
    const [searchValue, setSearchValue] = React.useState('');
    const [openModal, setOpenModal] = React.useState(false)

    const completedTodos = todos.filter(
      todo => !!todo.completed
    ).length; //metodo filter me crear치 un nuevo array que cumplan la condici칩n implementada
    const totalTodos = todos.length;

    const searchedTodos = todos.filter(
      (todo) => {
        const todoText = todo.text.toLowerCase();
        const searchText = searchValue.toLowerCase();
        return todoText.includes(searchText);
      }
    );
    const addTodo = (text) => {
      const newTodos = [...todos];
      newTodos.push({
        text,
        completed: false,
      })
      saveTodos(newTodos)
    }
    const completeTodo = (text) => {
      const newTodos = [...todos];
      const todoIndex = newTodos.findIndex(
        (todo) => todo.text === text
      );
      newTodos[todoIndex].completed = !newTodos[todoIndex].completed ;
      saveTodos(newTodos);
    };

    const deleteTodo = (text) => {
      const newTodos = [...todos];
      const todoIndex = newTodos.findIndex(
        (todo) => todo.text === text
      );
      newTodos.splice(todoIndex, 1); //metodo splice elimina(el indice colocado, elimina la cantidad hacia la derecha)
      saveTodos(newTodos);
    };

    return(
        <TodoContext.Provider value = {{
            loading,
            error,
            completedTodos,
            totalTodos,
            searchValue,
            setSearchValue,
            searchedTodos,
            completeTodo,
            deleteTodo,
            openModal,
            setOpenModal,
            addTodo,
            }}>
            {children}
        </TodoContext.Provider>
    )
}
export { TodoContext, TodoProvider}
