import React from "react";
import {User} from './components/User'
import {TodoCounter} from './components/TodoCounter'
import {TodoSearcher} from './components/TodoSearcher'
import {CreateItemButton} from './components/CreateItemButton'
import {NewTask} from './components/NewTask'
import { TodoList } from "./components/TodoList";
import { TodoItem } from "./components/TodoItem";
import {list} from './components/arr.js';

// localStorage.setItem('TODOS_V1', JSON.stringify(list))
// localStorage.removeItem('TODOS_V1', list)
function useLocalStorage(itemName){
  const [item, setItem] = React.useState()
  const localStorageTodos = localStorage.getItem(itemName)
  let parsedTodos
  if(!localStorageTodos){
    localStorage.setItem(itemName, JSON.stringify([]))
    parsedTodos = []
  } else{
      parsedTodos = JSON.parse(localStorageItem)
  }
  const saveItem = (newItem) => {
    localStorage.setItem(itemName, JSON.stringify(newTodos))
    setItem(newItem)
  } 
}
function App() {


  const [searchValue, setSearchValue] = React.useState('')
  const [todos, setTodos] = React.useState(parsedTodos)
  const completedTodos = todos.filter(todo => !!todo.completed).length
  const totalTodos = todos.length
  const searchTodo = todos.filter(todo => {
    const todoSearchText = todo.text.toLocaleLowerCase()
    const todoSearchValue = searchValue.toLocaleLowerCase()
    return todoSearchText.includes(todoSearchValue)
  })


  const completeTodo = (text) => {
    const newTodos = [...todos]
    const todoIndex = todos.findIndex((todo) => todo.text === text)
    newTodos[todoIndex].completed = !newTodos[todoIndex].completed
    saveTodos(newTodos)
  } 
  const deleteTodo = (text) => {
    const newTodos = [...todos]
    const todoIndex = todos.findIndex((todo) => todo.text === text)
    newTodos.splice(todoIndex, 1)
    saveTodos(newTodos)
  }
  return (
    <>
      <User />
      <TodoCounter completed={completedTodos} total={totalTodos}/>
      <TodoSearcher 
      searchValue = {searchValue}
      setSearchValue = {setSearchValue}/>
      <TodoList />
        {searchTodo.map(todo =>
        <TodoItem key = {todo.text} 
        text = {todo.text}
        completed={todo.completed}
        onComplete = { () => completeTodo(todo.text)}
        onDelete ={() => deleteTodo(todo.text)}
        />)}
      <CreateItemButton />
    </>
    )
}

export default App;
